CREATE OR REPLACE FUNCTION obter_tempo_entre_datas_util ( dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	M - Minutos
	HM - Horas e minutos
			*/
dt_aux_w				timestamp;
ie_dia_util_w			boolean;
qt_min_total_w			bigint := 0;
ds_retorno_w 			varchar(40) := '0';


BEGIN
dt_aux_w := dt_inicial_p;

if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	begin
	while(trunc(dt_aux_w,'dd') <= trunc(dt_final_p,'dd')) loop
		begin
		ie_dia_util_w := (obter_se_dia_util(dt_aux_w,wheb_usuario_pck.get_cd_estabelecimento) = 'S');
		if (ie_dia_util_w) then
			begin
			if (trunc(dt_aux_w,'dd') = trunc(dt_inicial_p,'dd')) and (trunc(dt_aux_w,'dd') < trunc(dt_final_p,'dd')) then
				qt_min_total_w := qt_min_total_w + obter_min_entre_datas(dt_inicial_p,fim_dia(dt_aux_w),1);
			elsif (trunc(dt_aux_w,'dd') > trunc(dt_inicial_p,'dd')) and (trunc(dt_aux_w,'dd') < trunc(dt_final_p,'dd')) then
				qt_min_total_w := qt_min_total_w + 1440;
			elsif (trunc(dt_aux_w,'dd') = trunc(dt_final_p,'dd')) then
				begin
				if (trunc(dt_inicial_p,'dd') = trunc(dt_final_p,'dd')) then
					qt_min_total_w := qt_min_total_w + obter_min_entre_datas(dt_inicial_p,dt_final_p,1);
				elsif (trunc(dt_inicial_p,'dd') < trunc(dt_final_p,'dd')) then
					qt_min_total_w := qt_min_total_w + obter_min_entre_datas(inicio_Dia(dt_aux_w),dt_final_p,1);
				end if;
				end;
			end if;
			end;
		end if;
		dt_aux_w := dt_aux_w + 1;
		end;
	end loop;
	if (ie_opcao_p = 'M') then
		ds_retorno_w := qt_min_total_w;
	elsif (ie_opcao_p = 'HM') then
		ds_retorno_w := obter_horas_minutos(qt_min_total_w);
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
