CREATE OR REPLACE FUNCTION obter_tempo_espera_agecons ( nr_atendimento_p bigint, dt_consulta_p timestamp) RETURNS bigint AS $body$
DECLARE

 
qt_min_espera_w bigint;
dt_inicio_consulta_w timestamp;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	dt_inicio_consulta_w := coalesce(dt_consulta_p,clock_timestamp());
	 
	select 	to_number(CASE WHEN coalesce(dt_entrada::text, '') = '' THEN  null  ELSE round((coalesce(dt_inicio_consulta_w, clock_timestamp()) - dt_entrada) * 1440) END ) 
	into STRICT	qt_min_espera_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	exception 
		when others then 
		qt_min_espera_w	:= 0;
	end;
end if;
return	qt_min_espera_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
