SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tempo_espera_clinica ( ie_clinica_p text, qt_pacientes_p bigint) RETURNS bigint AS $body$
DECLARE


qt_tempo_w  bigint;
qt_tempo_adic_w	bigint;
qt_medicos_w	integer;


BEGIN

qt_tempo_w := qt_pacientes_p * coalesce(obter_tempo_espera_pa(ie_clinica_p,null,null),15);

if (ie_clinica_p IS NOT NULL AND ie_clinica_p::text <> '') then
	select	coalesce(max(qt_tempo),0)
	into STRICT	qt_tempo_adic_w
	from	tempo_adic_espera_pa
	where	ie_clinica = ie_clinica_p
	and		dt_atualizacao between clock_timestamp() -(qt_tempo/60/24) and clock_timestamp();

	qt_tempo_w	:= qt_tempo_adic_w + qt_tempo_w;

end if;

if (ie_clinica_p IS NOT NULL AND ie_clinica_p::text <> '') then

	select	coalesce(max(qt_medico) ,0)
	into STRICT	qt_medicos_w
	from	painel_medico_pa
	where	ie_clinica = ie_clinica_p;

	if (qt_medicos_w > 0) then
		qt_tempo_w := trunc(qt_tempo_w / qt_medicos_w);
	end if;

end if;

return qt_tempo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
