CREATE OR REPLACE FUNCTION obter_tempo_espera_senha (dt_parametro timestamp) RETURNS varchar AS $body$
DECLARE


nr_minutos_w	bigint;
nr_horas_w	bigint;
ds_tempo_w	varchar(10);


BEGIN

nr_minutos_w := 0;
nr_horas_w   := 0;

select	sum(round((b.dt_entrada - a.dt_geracao_senha) * 1440))
into STRICT	nr_minutos_w
from	paciente_senha_fila a,
	atendimento_paciente b
where	b.nr_seq_pac_senha_fila = a.nr_sequencia
and	trunc(a.dt_geracao_senha) = trunc(clock_timestamp());

while(nr_minutos_w > 60) loop
	nr_horas_w := nr_horas_w + 1;
	nr_minutos_w := nr_minutos_w mod 60;
end loop;

ds_tempo_w := lpad(nr_horas_w, 2, '0') || ':' || lpad(nr_minutos_w, 2, '0');

return	ds_tempo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
