CREATE OR REPLACE FUNCTION obter_tempo_espera ( nr_seq_agecons_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_retorno_w	varchar(255);

/*
Tempo prevista de espera (minutos) - 'P'
Tempo real de espera (minutos) - 'R'
*/
BEGIN

if (nr_seq_agecons_p IS NOT NULL AND nr_seq_agecons_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
if (ie_opcao_p = 'P') then
	select 	max(pls_obter_dif_data_horario(a.dt_entrada, b.dt_agenda))
	into STRICT 	dt_retorno_w
	from 	atendimento_paciente a,
			agenda_consulta b
	where 	a.nr_atendimento = b.nr_atendimento
	and 	b.nr_sequencia = nr_seq_agecons_p;

elsif (ie_opcao_p = 'R') then
	select 	max(pls_obter_dif_data_horario(a.dt_entrada, b.dt_inicio_prescr))
	into STRICT 	dt_retorno_w
	from 	atendimento_paciente a,
			prescr_medica b
	where 	a.nr_atendimento = b.nr_atendimento
	and 	b.nr_seq_agecons = nr_seq_agecons_p;
end if;
end if;
return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
