CREATE OR REPLACE FUNCTION obter_tempo_exame (nr_seq_exame_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_tempo_w	bigint;


BEGIN
	select nr_seq_tempo
  	into STRICT nr_seq_tempo_w
		from (SELECT nr_seq_tempo, ie_prioridade
            from LAB_LOINC_TEMPO_EXAME
           where nr_seq_exame = nr_seq_exame_p and
                 coalesce(ie_situacao,'A') = 'A'
        order by ie_prioridade) alias1 LIMIT 1;

