CREATE OR REPLACE FUNCTION obter_tempo_hor_ag_exec (dt_agenda_p timestamp, nr_min_duracao_p bigint, dt_em_exame_p timestamp, dt_execucao_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	bigint;
dt_agenda_w	timestamp;


BEGIN

if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	dt_agenda_w	:= dt_agenda_p + nr_min_duracao_p/1440;

	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
		if (dt_em_exame_p > dt_agenda_w) then
			vl_retorno_w := round(((dt_em_exame_p - dt_agenda_w) * 1440),0);
		elsif (dt_execucao_p > dt_agenda_w) then
			vl_retorno_w := round(((dt_execucao_p - dt_agenda_w) * 1440),0);
		end if;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
