SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tempo_internacao ( cd_medico_p text, nr_seq_proc_interno_p bigint) RETURNS bigint AS $body$
DECLARE


qt_horas_internacao_w	bigint:=0;
cd_estabelecimento_w	smallint;


BEGIN

cd_estabelecimento_w	:=  coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);

if (coalesce(nr_seq_proc_interno_p,0) > 0) then
	select	coalesce(max(qt_horas_internacao),0)
	into STRICT	qt_horas_internacao_w
	from   	proc_interno_tempo
	where  	nr_seq_proc_interno 	= nr_seq_proc_interno_p
	and	coalesce(cd_medico,coalesce(cd_medico_p,'X')) = coalesce(cd_medico_p,'X')
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;
end if;

return	qt_horas_internacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
