CREATE OR REPLACE FUNCTION obter_tempo_lote ( nr_seq_lote_p bigint, ie_tipo_calculo_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_tipo_calculo: D - Dispensação, E - Entrega no setor, R - Recebimento pelo setor  */

/* ie_opcao_p: A - Tempo de atraso, F - Tempo que falta para limite de entrega */

dt_limite_disp_farm_w		timestamp;
dt_limite_entrega_setor_w	timestamp;
dt_limite_receb_setor_w		timestamp;

dt_parametro_w			timestamp;
dt_atual_w			timestamp		:= clock_timestamp();
nr_retorno_w			bigint	:= 0;


BEGIN

select	dt_limite_disp_farm,
	dt_limite_entrega_setor,
	dt_limite_receb_setor
into STRICT	dt_limite_disp_farm_w,
	dt_limite_entrega_setor_w,
	dt_limite_receb_setor_w
from	ap_lote
where	nr_sequencia	= nr_seq_lote_p;

if (ie_tipo_calculo_p = 'D') then
	dt_parametro_w	:= dt_limite_disp_farm_w;
elsif (ie_tipo_calculo_p = 'E') then
	dt_parametro_w	:= dt_limite_entrega_setor_w;
elsif (ie_tipo_calculo_p = 'R') then
	dt_parametro_w	:= dt_limite_receb_setor_w;
end if;


nr_retorno_w	:= obter_min_entre_datas(dt_atual_w, dt_parametro_w, 1);

if (nr_retorno_w < 0) then
	nr_retorno_w	:= nr_retorno_w * -1;
end if;

if (ie_opcao_p = 'A') then
	if (dt_atual_w < dt_parametro_w) then
		nr_retorno_w	:= 0;
	end if;
elsif (ie_opcao_p = 'F') then
	if (dt_atual_w > dt_parametro_w) then
		nr_retorno_w	:= 0;
	end if;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
