SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tempo_pac_hospital (dt_entrada_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_tempo_w		bigint;
ds_tempo_w		varchar(20);
ds_tempo_espera_w	varchar(20);
nr_horas_w		bigint;


BEGIN

nr_horas_w := 0;

qt_tempo_w := (clock_timestamp() - dt_entrada_p);

if (coalesce(qt_tempo_w,0) > 0) then

	while(qt_tempo_w > 60) loop
		nr_horas_w := nr_horas_w + 1;
		qt_tempo_w := qt_tempo_w mod 60;
	end loop;

	ds_tempo_w := lpad(nr_horas_w, 2, '0') || ':' || lpad(qt_tempo_w, 2, '0');

	ds_tempo_espera_w := ds_tempo_w;



end if;

return	ds_tempo_espera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
