CREATE OR REPLACE FUNCTION obter_tempo_plantao_medico (nr_seq_plantao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
H	- Horas
M	- Minutos
*/
qt_minuto_w		medico_plantao.qt_minuto%type;
dt_inicial_prev_w	medico_plantao.dt_inicial_prev%type;
dt_final_prev_w		medico_plantao.dt_final_prev%type;
ds_retorno_w		double precision;


BEGIN

select	sum(coalesce(qt_minuto,0))
into STRICT	qt_minuto_w
from	medico_plantao
where	nr_sequencia	= nr_seq_plantao_p;

if (qt_minuto_w	= 0) then

	if (ie_opcao_p	= 'M') then
		select	max(dt_inicial_prev),
			max(dt_final_prev)
		into STRICT	dt_inicial_prev_w,
			dt_final_prev_w
		from	medico_plantao
		where	nr_sequencia	= nr_seq_plantao_p;

		ds_retorno_w	:= coalesce(obter_minutos_intervalo(dt_final_prev_w,dt_inicial_prev_w,0),0);

	end if;
else
	ds_retorno_w	:= qt_minuto_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
