SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tempo_proced_agenda ( nr_seq_proc_interno_p bigint, cd_medico_p text, cd_agenda_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
qt_min_cirugia_w	bigint := null;
qt_min_higienizacao_w	bigint;
cd_estabelecimento_w	smallint;

/* 
ie_opcao_p 
TH - Tempo + Higienização 
H - Higienização 
T - Tempo 
*/
 
 

BEGIN 
 
select	coalesce(max(cd_estabelecimento),0) 
into STRICT	cd_estabelecimento_w 
from	agenda 
where	cd_agenda = cd_agenda_p;
 
select	max(qt_min_cirugia) 
into STRICT	qt_min_cirugia_w 
from	proc_interno_tempo 
where	nr_seq_proc_interno		= nr_seq_proc_interno_p 
and	coalesce(cd_medico,coalesce(cd_medico_p,'X')) = coalesce(cd_medico_p,'X') 
and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;
 
if (coalesce(qt_min_cirugia_w::text, '') = '') then 
	select	coalesce(max(qt_min_cirurgia),0) 
	into STRICT	qt_min_cirugia_w 
	from	proc_interno 
	where	nr_sequencia		= nr_seq_proc_interno_p;
end if;
 
select	coalesce(max(qt_min_higienizacao),0) 
into STRICT	qt_min_higienizacao_w 
from	proc_interno 
where	nr_sequencia			= nr_seq_proc_interno_p;
 
if (ie_opcao_p = 'T') then 
	return qt_min_cirugia_w;
elsif (ie_opcao_p = 'H') then 
	return qt_min_higienizacao_w;
else 
	return qt_min_cirugia_w + qt_min_higienizacao_w;
end if;	
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
