CREATE OR REPLACE FUNCTION obter_tempo_restante_triagem ( nr_seq_triagem_p bigint) RETURNS varchar AS $body$
DECLARE


qt_minutos_rest_w		bigint;
qt_tempo_atend_tria_w   smallint;
ie_status_paciente_w	triagem_pronto_atend.ie_status_paciente%type;
dt_fim_triagem_w		triagem_pronto_atend.dt_fim_triagem%type;
dt_triagem_incompleta_w	triagem_pronto_atend.dt_triagem_incompleta%type;
nr_atendimento_w        triagem_pronto_atend.nr_atendimento%type;
dt_atend_medico_w		atendimento_paciente.dt_atend_medico%type;


BEGIN

	select 	ie_status_paciente,
			dt_fim_triagem,
			dt_triagem_incompleta,
			obter_tempo_atend_triagem(nr_seq_classif),
            nr_atendimento
	into STRICT	ie_status_paciente_w,
			dt_fim_triagem_w,
			dt_triagem_incompleta_w,
			qt_tempo_atend_tria_w,
            nr_atendimento_w
	from 	triagem_pronto_atend
	where 	nr_sequencia = nr_seq_triagem_p;
	
	if (ie_status_paciente_w = 'T') then
        qt_minutos_rest_w		:= round((coalesce(dt_fim_triagem_w, dt_triagem_incompleta_w) + (1/1440*qt_tempo_atend_tria_w)- clock_timestamp()) * 1440);
	elsif (ie_status_paciente_w = 'A') then
		select	dt_atend_medico
		into STRICT	dt_atend_medico_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_w;

		qt_minutos_rest_w	:= round((coalesce(dt_fim_triagem_w, dt_triagem_incompleta_w) + (1 / 1440 * qt_tempo_atend_tria_w) - dt_atend_medico_w) * 1440);
	end if;

	return qt_minutos_rest_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
