CREATE OR REPLACE FUNCTION obter_tempo_retencao_protocolo (qt_tempo_retencao_p bigint, ie_tempo_retencao_p text) RETURNS bigint AS $body$
DECLARE

qt_tempo_retencao_w	bigint;

BEGIN
if (coalesce(qt_tempo_retencao_p,0) <> 0) and (ie_tempo_retencao_p IS NOT NULL AND ie_tempo_retencao_p::text <> '')then
	begin
	if (ie_tempo_retencao_p = 'A') then
		qt_tempo_retencao_w := (qt_tempo_retencao_p * 525600);
	elsif (ie_tempo_retencao_p = 'D') then
		qt_tempo_retencao_w := qt_tempo_retencao_p * 1440;
	elsif (ie_tempo_retencao_p = 'H') then
		qt_tempo_retencao_w := ((qt_tempo_retencao_p * 60) / 1440);
	elsif (ie_tempo_retencao_p = 'M') then
		qt_tempo_retencao_w := qt_tempo_retencao_p * 43200;
	end if;
	end;
end if;

return	qt_tempo_retencao_w;

end;
