CREATE OR REPLACE FUNCTION obter_tempo_senha (nr_seq_senha_p bigint) RETURNS varchar AS $body$
DECLARE


qt_tempo_atend_w	bigint;
ds_tempo_w		varchar(10);
ds_tempo_espera_w	varchar(20);
nr_horas_w		bigint;


BEGIN

nr_horas_w   := 0;

if (coalesce(nr_seq_senha_p,0) > 0) then

	select	substr(coalesce(obter_min_entre_datas(dt_inicio_atendimento,coalesce(dt_fim_atendimento,clock_timestamp()),1),0),1,30)
	into STRICT	qt_tempo_atend_w
	from	paciente_senha_fila
	where	nr_sequencia = nr_seq_senha_p;


	while(qt_tempo_atend_w > 60) loop
		nr_horas_w := nr_horas_w + 1;
		qt_tempo_atend_w := qt_tempo_atend_w mod 60;
	end loop;

	ds_tempo_w := lpad(nr_horas_w, 2, '0') || ':' || lpad(qt_tempo_atend_w, 2, '0');

	ds_tempo_espera_w := ds_tempo_w;

	If (coalesce(ds_tempo_espera_w::text, '') = '') then
		ds_tempo_espera_w := '00:00:00';
	end if;

end if;

return	ds_tempo_espera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tempo_senha (nr_seq_senha_p bigint) FROM PUBLIC;

