CREATE OR REPLACE FUNCTION obter_tempo_status_pac_agexame (nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


dt_chegada_w		timestamp;
dt_status_pac_w		timestamp;
qt_retorno_w		bigint;



BEGIN

select	max(dt_chegada),
	max(dt_status_pac)
into STRICT	dt_chegada_w,
	dt_status_pac_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

if (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') and (dt_status_pac_w IS NOT NULL AND dt_status_pac_w::text <> '') and (dt_status_pac_w < dt_chegada_w) then
	qt_retorno_w := round(((dt_chegada_w - dt_status_pac_w) * 1440),0);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
