CREATE OR REPLACE FUNCTION obter_tempo_tot_impl ( nr_seq_modulo_p bigint, nr_seq_funcao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opçcao_p
P - Pequeno
M - Médio
G - Grande
GG - Gigante
*/
nr_retorno_w		double precision;


BEGIN
if (nr_seq_modulo_p = 0) then 	-- valores por função
	begin
	if (ie_opcao_p = 'P') then
		begin
		select	coalesce(sum(qt_tempo_param_peq),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	cd_funcao = nr_seq_funcao_p;
		end;
	elsif (ie_opcao_p = 'M') then
		begin
		select	coalesce(sum(qt_tempo_param_med),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	cd_funcao = nr_seq_funcao_p;
		end;
	elsif (ie_opcao_p = 'G') then
		begin
		select	coalesce(sum(qt_tempo_param_gra),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	cd_funcao = nr_seq_funcao_p;
		end;
	elsif (ie_opcao_p = 'GG') then
		begin
		select	coalesce(sum(qt_tempo_param_gg),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	cd_funcao = nr_seq_funcao_p;
		end;
	end if;
	end;
else					-- Valor por módulo
	begin
		if (ie_opcao_p = 'P') then
		begin
		select	coalesce(sum(qt_tempo_param_peq),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	nr_seq_mod_impl = nr_seq_modulo_p;
		end;
	elsif (ie_opcao_p = 'M') then
		begin
		select	coalesce(sum(qt_tempo_param_med),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	nr_seq_mod_impl = nr_seq_modulo_p;
		end;
	elsif (ie_opcao_p = 'G') then
		begin
		select	coalesce(sum(qt_tempo_param_gra),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	nr_seq_mod_impl = nr_seq_modulo_p;
		end;
	elsif (ie_opcao_p = 'GG') then
		begin
		select	coalesce(sum(qt_tempo_param_gg),0)
		into STRICT	nr_retorno_w
		from	funcao
		where	nr_seq_mod_impl = nr_seq_modulo_p;
		end;
	end if;
	end;
end if;

return	nr_retorno_w;

end;
$body$
