CREATE OR REPLACE FUNCTION obter_terap_dose_solucao (nr_seq_prescr_sol_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_solucao_w  	cpoe_material.ie_tipo_solucao%type;
qt_dose_terapeutica_w 	cpoe_material.qt_dose_terapeutica%type;
cd_unid_medic_terap_w 	cpoe_material.cd_unid_medic_terap%type;
ds_solucao_terap_dose_w	varchar(255) := '';
ds_unid_medic_terap_w 	varchar(100);
nr_seq_mat_cpoe_w 	bigint;


BEGIN

if (nr_seq_prescr_sol_p IS NOT NULL AND nr_seq_prescr_sol_p::text <> '' AND nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	max(nr_seq_mat_cpoe)
	into STRICT 	nr_seq_mat_cpoe_w
	from prescr_material
	where nr_sequencia_solucao	= nr_seq_prescr_sol_p
	and nr_prescricao	= nr_prescricao_p;

	if (nr_seq_mat_cpoe_w IS NOT NULL AND nr_seq_mat_cpoe_w::text <> '') then
		select 	max(a.ie_tipo_solucao),
			max(a.qt_dose_terapeutica),
			max(a.cd_unid_medic_terap)
		into STRICT	ie_tipo_solucao_w,
			qt_dose_terapeutica_w,
			cd_unid_medic_terap_w
		from cpoe_material a
		where a.nr_sequencia   = nr_seq_mat_cpoe_w;

		if (ie_tipo_solucao_w ='V') then

			select 	max(substr(obter_desc_expressao(cd_exp_dose,ds_dose_terapeutica),1,100))
			into STRICT 	ds_unid_medic_terap_w
			from regra_dose_terap
			where nr_sequencia       = cd_unid_medic_terap_w;

			ds_solucao_terap_dose_w := obter_desc_expressao(288274) || ': ' || cpoe_dose_formatada(qt_dose_terapeutica_w) ||'  ' || ds_unid_medic_terap_w;
		end if;

	end if;

end if;

return ds_solucao_terap_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
