CREATE OR REPLACE FUNCTION obter_terceiro_medico ( cd_medico_p text, cd_estabelecimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

 
nr_seq_terceiro_w	bigint;
qt_pessoa_terceiro_w	bigint;


BEGIN 
 
select	coalesce(min(nr_sequencia),0) 
into STRICT	nr_seq_terceiro_w 
from 	terceiro 
where	cd_pessoa_fisica	= cd_medico_p 
and	cd_estabelecimento	= cd_estabelecimento_p 
and	coalesce(ie_utilizacao,'A')	in ('A','R') 
and	ie_situacao		= 'A' 
and	dt_referencia_p		between coalesce(trunc(dt_inicio_vigencia,'dd'), dt_referencia_p - 1) and fim_dia(coalesce(dt_fim_vigencia, dt_referencia_p + 1));
 
if (coalesce(nr_seq_terceiro_w,0) = 0) then 
 
	select	min(a.nr_seq_terceiro) 
	into STRICT	nr_seq_terceiro_w 
	from	terceiro b, 
		terceiro_pessoa_fisica a 
	where	a.cd_pessoa_fisica	= cd_medico_p 
	and	a.nr_seq_terceiro	= b.nr_sequencia 
	and	b.cd_estabelecimento	= cd_estabelecimento_p 
	and	b.ie_situacao		= 'A' 
	and	coalesce(b.ie_utilizacao,'A') in ('A','R') 
	and	dt_referencia_p		between coalesce(trunc(b.dt_inicio_vigencia,'dd'), dt_referencia_p - 1) and fim_dia(coalesce(b.dt_fim_vigencia, dt_referencia_p + 1)) 
	and	dt_referencia_p		between	coalesce(trunc(a.dt_inicio_vigencia,'dd'), dt_referencia_p - 1) and fim_dia(coalesce(a.dt_fim_vigencia, dt_referencia_p + 1)) 
	and	coalesce(a.ie_repasse,'S')	= 'S';
 
else	/* conferir se a PF do terceiro está em vigência */
 
 
	select	count(*) 
	into STRICT	qt_pessoa_terceiro_w 
	from	terceiro_pessoa_fisica a 
	where	a.cd_pessoa_fisica	= cd_medico_p 
	and	a.nr_seq_terceiro	= nr_seq_terceiro_w;
 
	if (qt_pessoa_terceiro_w	> 0) then 
 
		select	coalesce(min(a.nr_seq_terceiro),0) 
		into STRICT	nr_seq_terceiro_w 
		from	terceiro_pessoa_fisica a 
		where	coalesce(a.ie_repasse,'S')	= 'S' 
		and	dt_referencia_p		between coalesce(trunc(a.dt_inicio_vigencia,'dd'), dt_referencia_p - 1) and fim_dia(coalesce(a.dt_fim_vigencia, dt_referencia_p + 1)) 
		and	a.cd_pessoa_fisica	= cd_medico_p 
		and	a.nr_seq_terceiro	= nr_seq_terceiro_w;
 
	end if;
 
end if;
 
if (coalesce(nr_seq_terceiro_w,0) = 0) then 
 
	select	coalesce(min(nr_seq_terceiro),0) 
	into STRICT	nr_seq_terceiro_w 
	from 	Parametro_faturamento 
	where	cd_estabelecimento	= cd_estabelecimento_p;
 
end if;
 
return	nr_seq_terceiro_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
