CREATE OR REPLACE FUNCTION obter_termino_previsto_adm (nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_termino_previsto_w	timestamp;


BEGIN

select 	max(dt_administracao + coalesce(qt_hora_aplic,0)/24 + coalesce(qt_min_aplic,0)/24/60)
into STRICT 	dt_termino_previsto_w
from 	can_ordem_prod
where 	nr_sequencia = nr_sequencia_P;


return	dt_termino_previsto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
