SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_texto_centralizado ( ds_texto_p text, nr_largura_total_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ds_texto_w		varchar(255);
nr_espaco_w		smallint;



BEGIN


ds_texto_w		:= trim(both ds_texto_p);

nr_espaco_w		:= dividir(nr_largura_total_p - length(ds_texto_w), 2) + length(ds_texto_w);

ds_retorno_w		:= lpad(ds_texto_w, nr_espaco_w, ' ');



return ds_retorno_w;

End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
