CREATE OR REPLACE FUNCTION obter_texto_entre_caracter ( ds_texto_p text, ie_caracter_inicio_p text, ie_caracter_fim_p text) RETURNS varchar AS $body$
DECLARE


lista_frases_w		lista_varchar_pck.tabela_varchar;
ds_frases_w		varchar(30000);
ds_retorno_w		varchar(4000);
pos_car_ini_w		integer;

BEGIN
if (ds_texto_p IS NOT NULL AND ds_texto_p::text <> '') then
	ds_frases_w := substr(ds_texto_p, 1, 20000);

	lista_frases_w := obter_lista_string2(ds_frases_w, ie_caracter_fim_p);

	for	i in lista_frases_w.first..lista_frases_w.last loop

		select	CASE WHEN position(ie_caracter_inicio_p in lista_frases_w(i))=0 THEN length(lista_frases_w(i))  ELSE position(ie_caracter_inicio_p in lista_frases_w(i)) END  +1
		into STRICT	pos_car_ini_w
		;

		ds_retorno_w	:= ds_retorno_w || substr(lista_frases_w(i),
					pos_car_ini_w,2000) || chr(13) || chr(10);

	end loop;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
