CREATE OR REPLACE FUNCTION obter_texto_entre_macro ( ds_texto_p text, ds_macro_p text, ds_caracter_fim_p text ) RETURNS varchar AS $body$
DECLARE


lista_frases_w		lista_varchar_pck.tabela_varchar;
ds_frases_w		varchar(20000);
ds_retorno_w		varchar(4000);
pos_macro_w		integer;

BEGIN
if (ds_texto_p IS NOT NULL AND ds_texto_p::text <> '') then
	begin
	ds_frases_w := substr(ds_texto_p, 1, 20000);

	lista_frases_w := obter_lista_string2(ds_frases_w, ds_caracter_fim_p);

	for	i in lista_frases_w.first..lista_frases_w.last loop
		begin
		pos_macro_w := position(ds_macro_p in lista_frases_w(i));

		if (pos_macro_w > 0) then
			begin
			ds_retorno_w := substr(lista_frases_w(i), pos_macro_w + length(ds_macro_p));
			exit;
			end;
		end if;
		end;
	end loop;
	end;
end if;

return	ds_retorno_w;

end;
