CREATE OR REPLACE FUNCTION obter_texto_inf_adic_cir ( cd_material_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN
if (coalesce(cd_material_p,0) > 0) then
	select	wheb_mensagem_pck.get_texto(309598) || ': '|| substr(a.cd_material_estoque || ' - ' || substr(obter_dados_material(a.cd_material_estoque,'D'),1,80),1,255) -- Material estoque
	into STRICT	ds_retorno_w
	from	material a
	where 	a.cd_material = cd_material_p;
end if;

if (coalesce(nr_seq_material_p,0) > 0) then
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		ds_retorno_w := ds_retorno_w || chr(13) || chr(10) || wheb_mensagem_pck.get_texto(309597) || ': '||substr(obter_desc_kit(nr_seq_material_p),1,100); -- Kit material
	else
		ds_retorno_w := wheb_mensagem_pck.get_texto(309597) || ': '|| substr(obter_desc_kit(nr_seq_material_p),1,100); -- Kit material
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
