CREATE OR REPLACE FUNCTION obter_texto_orientacao_adm_md ( qt_volume_p bigint, nr_casas_diluicao_p bigint, ds_uom_p text, cd_intervalo_p text, ds_interv_prescr_p text, ie_via_aplicacao_p text, ds_via_p text, ds_tempo_p text, ds_retorno_inf_p text ) RETURNS varchar AS $body$
DECLARE

  ds_administrar_w varchar(2000);
  qt_resul_calc_w  varchar(20);

BEGIN
	
	qt_resul_calc_w := formatar_dosage_md(round(qt_volume_p, nr_casas_diluicao_p));

	ds_administrar_w := wheb_mensagem_pck.get_texto(307088) || ' ' || qt_resul_calc_w || ' ' || ds_uom_p;
	if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	  ds_administrar_w :=  ds_administrar_w ||' (' || trim(both ds_interv_prescr_p);
	  if (ie_via_aplicacao_p IS NOT NULL AND ie_via_aplicacao_p::text <> '') then
	    ds_administrar_w :=  ds_administrar_w || ' ' || lower(wheb_mensagem_pck.get_texto(312325)) || ' ' || ds_via_p;
	  end if;
	  ds_administrar_w := ds_administrar_w || ')';
	end if;

	ds_administrar_w := ds_administrar_w || ds_tempo_p || ds_retorno_inf_p || '. ' || chr(13);

  return ds_administrar_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
