CREATE OR REPLACE FUNCTION obter_texto_padrao_anamnese ( cd_profissional_p text, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_texto_w		bigint;
cd_especialidade_w	bigint;
ie_tipo_atendimento_w	bigint	:= 0;
ie_clinica_w		bigint	:= 0;
cd_setor_atendimento_w	bigint	:= 0;
nm_usuario_w		varchar(50);
cd_estabelecimento_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_seq_texto 
	into STRICT	nr_seq_texto_w 
	from	anamnese_texto_padrao 
	where	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w 
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w 
	and	coalesce(ie_clinica,ie_clinica_w)			= ie_clinica_w 
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)= cd_setor_atendimento_w 
	and	coalesce(nm_usuario_config,nm_usuario_w)		= nm_usuario_w 
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w 
	order by coalesce(nm_usuario_config,'AAAAAAA'), 
		coalesce(ie_tipo_atendimento, 0), 
		 coalesce(cd_setor_atendimento, 0), 
		 coalesce(ie_clinica,0), 
		 coalesce(cd_estabelecimento, 0);

BEGIN
cd_especialidade_w	:= coalesce(obter_especialidade_medico(cd_profissional_p,'C'),0);
nm_usuario_w		:= coalesce(wheb_usuario_pck.get_nm_usuario,'AAAAAA');
cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,1);
 
if (coalesce(nr_atendimento_p,0)	> 0) then 
	select	ie_tipo_atendimento, 
		coalesce(ie_clinica,0), 
		cd_setor_atendimento 
	into STRICT	ie_tipo_atendimento_w, 
		ie_clinica_w, 
		cd_setor_atendimento_w 
	from	resumo_atendimento_paciente_v 
	where	nr_atendimento	= nr_atendimento_p;
end if;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_texto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;
 
 
return nr_seq_texto_w;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
