CREATE OR REPLACE FUNCTION obter_texto_padrao_desfecho (cd_estabelecimento_p bigint, ie_desfecho_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	bigint;
cd_perfil_ativo_w	bigint;


BEGIN

cd_perfil_ativo_w	:= obter_perfil_ativo;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from 	texto_padrao
where nr_seq_item_pront = 1103
and	coalesce(ie_opcao_padrao,'S')	= 'S'
and ie_desfecho = ie_desfecho_p
and	((coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p) or coalesce(cd_estabelecimento_p::text, '') = '')
and	((coalesce(cd_setor_atendimento::text, '') = '') or (obter_se_setor_usuario(cd_setor_atendimento, nm_usuario_p) = 'S'))
and (Obter_Se_Texto_Padrao_lib(nr_sequencia) = 'S')
and (coalesce(cd_perfil,cd_perfil_ativo_w) = cd_perfil_ativo_w);

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
