CREATE OR REPLACE FUNCTION obter_texto_padrao_evol_palm_atx ( cd_tipo_evolucao_p text, cd_profissional_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

/*
C - CÃ³digo
*/
nr_seq_texto_w		bigint;
cd_especialidade_w	bigint := 0;
ie_tipo_atendimento_w	bigint	:= 0;
ie_clinica_w		bigint	:= 0;
cd_setor_atendimento_w	bigint	:= 0;
nm_usuario_w		varchar(50);
cd_perfil_w		bigint;
ds_retorno_w	varchar(32000);
nr_texto_rtf_w	bigint;
ds_retorno_ww	varchar(32000);

C01 CURSOR FOR
	SELECT	nr_seq_texto
	into STRICT	nr_seq_texto_w
	from	tipo_evolucao_texto
	where	cd_tipo_evolucao	= cd_tipo_evolucao_p
	and	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w
	and	coalesce(ie_clinica,ie_clinica_w)			= ie_clinica_w
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)= cd_setor_atendimento_w
	and	coalesce(nm_usuario_config,nm_usuario_w)		= nm_usuario_w
	and	coalesce(cd_perfil,cd_perfil_w)			= cd_perfil_w
	order by coalesce(nm_usuario_config,'AAAAAAA'),
		coalesce(cd_perfil,0),
		coalesce(ie_tipo_atendimento, 0),
		 coalesce(cd_setor_atendimento, 0),
		 coalesce(ie_clinica,0);
BEGIN
BEGIN
nr_texto_rtf_w := NULL;
cd_especialidade_w	:= coalesce(obter_especialidade_medico(cd_profissional_p,'C'),0);
nm_usuario_w		:= coalesce(wheb_usuario_pck.get_nm_usuario,'AAAAAA');
cd_perfil_w		:= coalesce(obter_perfil_ativo,0);

if (coalesce(nr_atendimento_p,0)	> 0) then
	select	ie_tipo_atendimento,
		coalesce(ie_clinica,0),
		cd_setor_atendimento
	into STRICT	ie_tipo_atendimento_w,
		ie_clinica_w,
		cd_setor_atendimento_w
	from	resumo_atendimento_paciente_v
	where	nr_atendimento	= nr_atendimento_p;
end if;

open C01;
loop
fetch C01 into
	nr_seq_texto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (coalesce(nr_seq_texto_w::text, '') = '') then
	select	max(nr_seq_texto)
	into STRICT	nr_seq_texto_w
	from	tipo_evolucao
	where	cd_tipo_evolucao	= cd_tipo_evolucao_p;
end if;

if (nr_seq_texto_w IS NOT NULL AND nr_seq_texto_w::text <> '') then

	nr_texto_rtf_w := converte_rtf_string('select ds_texto from texto_padrao where nr_sequencia = :nr_seq_texto_w', nr_seq_texto_w, nm_usuario_w, nr_texto_rtf_w);

	select	ds_texto
	into STRICT	ds_retorno_w
	from	tasy_conversao_rtf
	where	nr_sequencia = nr_texto_rtf_w;

	if (coalesce(ds_retorno_w::text, '') = '') or (length(trim(both ds_retorno_w)) <= 0) then
		select ds_texto
		into STRICT   ds_retorno_w
		from   texto_padrao
		where  nr_sequencia = nr_seq_texto_w;
	end if;

	ds_retorno_ww := Substituir_Macros_Tasy(ds_retorno_w, '', '', nr_atendimento_p, ds_retorno_ww);


end if;
exception
when others then
	null;
end;
return coalesce(ds_retorno_ww,ds_retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
