CREATE OR REPLACE FUNCTION obter_texto_padrao_item (nr_seq_item_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint default null, ie_evolucao_p text default null, ie_justificativa_p text default null, cd_especialidade_p bigint default null, ie_sem_especialidade_p text default null) RETURNS varchar AS $body$
DECLARE

/* 
C - CÃ³digo 
*/
 
 
nr_sequencia_w		bigint;
cd_perfil_ativo_w	bigint;


BEGIN 
 
cd_perfil_ativo_w	:= obter_perfil_ativo;
 
if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then 
	 
	select	max(coalesce(nr_sequencia,0)) 
	into STRICT	nr_sequencia_w 
	from	texto_padrao 
	where	nr_seq_item_pront = nr_seq_item_p 
	and	ie_opcao_padrao = 'S' 
	and	((coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p) or coalesce(cd_estabelecimento_p::text, '') = '') 
	and	((coalesce(ie_evolucao_clinica,ie_evolucao_p) = ie_evolucao_p) or coalesce(ie_evolucao_p::text, '') = '') 
	and	((coalesce(ie_tipo_justificativa,ie_justificativa_p) = ie_justificativa_p) or coalesce(ie_justificativa_p::text, '') = '') 
	and	((cd_especialidade = cd_especialidade_p) or ((coalesce(cd_especialidade::text, '') = '') and (ie_sem_especialidade_p = 'S')) or (coalesce(cd_especialidade_p,0) = 0)) 
	and (Obter_Se_Texto_Padrao_lib(nr_sequencia) = 'S') 
	and (coalesce(cd_perfil,cd_perfil_ativo_w) = cd_perfil_ativo_w);
	 
end if;
 
if (ie_opcao_p = 'C') then 
 
	return nr_sequencia_w;
 
end if;
	 
end;
$body$
