CREATE OR REPLACE FUNCTION obter_texto_padrao_ocorrencia ( cd_tipo_ocorrencia_p text ) RETURNS bigint AS $body$
DECLARE


nr_seq_texto_w	bigint;
cd_perfil_w		bigint;


BEGIN

cd_perfil_w		:= coalesce(obter_perfil_ativo,0);


select	max(nr_seq_texto)
into STRICT	nr_seq_texto_w
from	tipo_ocor_turno_texto
where	cd_tipo_ocorrencia = cd_tipo_ocorrencia_p
and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
order by coalesce(cd_perfil,0);


return	nr_seq_texto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
