CREATE OR REPLACE FUNCTION obter_texto_procedencia_prot ( nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(4000);
ds_procedencia_w	varchar(40);
qt_contas_w		bigint;

C01 CURSOR FOR 
	SELECT	substr(obter_desc_procedencia(b.cd_procedencia),1,40) ds_procedencia, 
		count(*) qt_contas 
	from	conta_paciente a, 
		atendimento_paciente b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	a.nr_seq_protocolo = nr_seq_protocolo_p 
	group by	b.cd_procedencia 
	order by 	ds_procedencia;
	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	ds_procedencia_w, 
	qt_contas_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	 
	ds_retorno_w	:= substr(ds_retorno_w || ds_procedencia_w || ' = ' || qt_contas_w || chr(10) ,1,4000);	
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
