CREATE OR REPLACE FUNCTION obter_texto_senha_email_pj ( cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_texto_w	varchar(2000);
ds_senha_w	varchar(20);


BEGIN
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
	select	max(ds_senha)
	into STRICT	ds_senha_w
	from	pessoa_juridica
	where	cd_cgc = cd_cgc_p;

	if (ds_senha_w IS NOT NULL AND ds_senha_w::text <> '') then
		begin
		select	max(ds_informacao)
		into STRICT	ds_texto_w
		from	dic_objeto
		where	nr_sequencia = 24389;

		if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') then
			begin
			ds_texto_w := replace(ds_texto_w,'#@ds_senha#@',ds_senha_w);
			end;
		end if;
		end;
	end if;
	end;
end if;
return ds_texto_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
