CREATE OR REPLACE FUNCTION obter_texto_tasy ( nr_seq_texto_p bigint, nr_seq_idioma_p bigint) RETURNS varchar AS $body$
DECLARE


ds_texto_w		varchar(4000) := null;


BEGIN

if (nr_seq_texto_p IS NOT NULL AND nr_seq_texto_p::text <> '') then
	begin

	select	obter_desc_expressao_idioma(cd_exp_informacao,ds_informacao,coalesce(nr_seq_idioma_p,philips_param_pck.get_nr_seq_idioma))
	into STRICT	ds_texto_w
	from	dic_objeto
	where	nr_sequencia = nr_seq_texto_p;
	exception
	when others then
		ds_texto_w := null;
	end;

end	if;

return	ds_texto_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
