SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipos_isolamentos_cih ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* 	ie_opcao:
	'C' - Códigos;
	'D' - Descrições;
*/
C01 CURSOR FOR
	SELECT	CASE WHEN ie_opcao_p='C' THEN  to_char(b.nr_seq_tipo_isolamento)  ELSE a.ds_tipo_isolamento END
	from	cih_tipo_isolamento a,
		cih_atend_isolamento b
	where	b.nr_seq_tipo_isolamento = a.nr_sequencia
	and	b.nr_atendimento = nr_atendimento_p
	order by coalesce(a.nr_seq_prioridade,9999),
		b.dt_atualizacao_nrec desc;

ds_retorno_w		varchar(2000);
ds_valor_w		varchar(80);
ie_paciente_isolado_w	varchar(1);


BEGIN

select 	coalesce(max(ie_paciente_isolado),'N')
into STRICT	ie_paciente_isolado_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

if (ie_paciente_isolado_w = 'S') then
	open C01;
	loop
	fetch C01 into
		ds_valor_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_valor_w;
		else
			ds_retorno_w := ds_retorno_w || ', ' || ds_valor_w;
		end if;
		end;
	end loop;
	close C01;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
