CREATE OR REPLACE FUNCTION obter_tipo_acomod_atend ( nr_atendimento_p bigint, ie_codigo_desc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(40) := null;
cd_tipo_acomod_w		smallint;
dt_alta_w			timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(cd_tipo_acomodacao)
	into STRICT	cd_tipo_acomod_w
	from	Atend_categoria_convenio a
	where	a.nr_atendimento		= nr_atendimento_p
	and	a.dt_inicio_vigencia	=
			(SELECT	max(dt_inicio_vigencia)
			from	Atend_categoria_convenio b
			where	nr_atendimento	= nr_atendimento_p);

end if;

if (ie_codigo_desc_p = 'D') then
	select	substr(obter_desc_tipo_acomod(cd_tipo_acomod_w),1,40)
	into STRICT	ds_retorno_w
	;
elsif (ie_codigo_desc_p 	 = 'C') then
	ds_retorno_w	:=	cd_tipo_acomod_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
