CREATE OR REPLACE FUNCTION obter_tipo_acomod_atual ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_tipo_acomodacao_w	varchar(100);
cd_tipo_acomodacao_w	smallint;
ds_retorno_w		varchar(100);
ds_desc_acomod_w	varchar(255);

BEGIN
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
SELECT 	max(SUBSTR(obter_desc_tipo_acomod(cd_tipo_acomodacao),1,100)), 
		max(cd_tipo_acomodacao) 
	into STRICT	ds_tipo_acomodacao_w, 
		cd_tipo_acomodacao_w 
	from	atend_paciente_unidade a 
	where	a.nr_seq_interno = (SELECT obter_atepacu_paciente(nr_atendimento_p, 'A') );
	 
	select	coalesce(substr(Obter_Unidade_Atendimento(nr_atendimento_p,'IAA','DTA'),1,255),'') 
	into STRICT	ds_desc_acomod_w 
	;
end if;
 
if (ie_opcao_p 	 = 'D') then 
	ds_retorno_w	:=	ds_tipo_acomodacao_w;
elsif (ie_opcao_p 	 = 'C') then 
	ds_retorno_w	:=	cd_tipo_acomodacao_w;
elsif (ie_opcao_p 	 = 'DTA') then 
	ds_retorno_w  := ds_desc_acomod_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
