CREATE OR REPLACE FUNCTION obter_tipo_acomod_data ( nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE



cd_tipo_acomodacao_w		bigint	:= null;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	select	coalesce(max(cd_tipo_acomodacao),0)
	into STRICT	cd_tipo_acomodacao_w
	from 	atend_paciente_unidade a
	where	a.nr_atendimento 		= nr_atendimento_p
	and	nr_seq_interno	=
		(SELECT	max(coalesce(nr_seq_interno,0))
		from 	atend_paciente_unidade b
		where	nr_atendimento 	= nr_atendimento_p
		and	coalesce(b.dt_saida_unidade, b.dt_entrada_unidade + 9999) <= dt_referencia_p);
end if;

return cd_tipo_acomodacao_w;

end;
