CREATE OR REPLACE FUNCTION obter_tipo_acomod_leito ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(40);
cd_tipo_acomod_w	smallint;
ds_tipo_acomod_w	varchar(40);
qt_registros_w		bigint;


BEGIN

select 	count(1)
into STRICT	qt_registros_w
from	unidade_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_p
and	cd_unidade_basica	= cd_unidade_basica_p
and	cd_unidade_compl	= CASE WHEN cd_unidade_compl_p='' THEN ' '  ELSE cd_unidade_compl_p END;

if (qt_registros_w > 0) then
	begin
	select	cd_tipo_acomodacao,
		substr(obter_desc_tipo_acomod(cd_tipo_acomodacao),1,40)
	into STRICT	cd_tipo_acomod_w,
		ds_tipo_acomod_w
	from	unidade_atendimento
	where	cd_setor_atendimento	= cd_setor_atendimento_p
	and	cd_unidade_basica	= cd_unidade_basica_p
	and	cd_unidade_compl	= CASE WHEN cd_unidade_compl_p='' THEN ' '  ELSE cd_unidade_compl_p END;
	end;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_tipo_acomod_w;
else
	ds_retorno_w := ds_tipo_acomod_w;
end if;

return	ds_retorno_w;

end;
$body$
