CREATE OR REPLACE FUNCTION obter_tipo_agenda_seq (nr_seq_agenda_p bigint, ie_agenda_p text) RETURNS bigint AS $body$
DECLARE


cd_agenda_w		bigint;
cd_tipo_agenda_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_agenda_p IS NOT NULL AND ie_agenda_p::text <> '') then
	if (ie_agenda_p in ('E','CI')) then
		select	coalesce(max(cd_agenda),0)
		into STRICT	cd_agenda_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_p;

		if (cd_agenda_w > 0) then
			select	coalesce(max(cd_tipo_agenda),0)
			into STRICT	cd_tipo_agenda_w
			from	agenda
			where	cd_agenda = cd_agenda_w;
		end if;
	end if;
end if;

return cd_tipo_agenda_w;

end;
