CREATE OR REPLACE FUNCTION obter_tipo_arquivo_prot_conv (cd_convenio_p bigint, ie_tipo_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_arquivo_w	varchar(01)	:= 'P';

c01 CURSOR FOR
	SELECT	ie_tipo_arquivo
	from	conv_regra_envio_protocolo
	where	cd_convenio					= cd_convenio_p
	and	coalesce(ie_tipo_protocolo, ie_tipo_protocolo_p)	= ie_tipo_protocolo_p
	order by ie_tipo_protocolo desc;


BEGIN

open	c01;
loop
fetch	c01 into ie_tipo_arquivo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ie_tipo_arquivo_w	:= ie_tipo_arquivo_w;

	end;
end loop;
close c01;

return	ie_tipo_arquivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
