SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_atend_abramge ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
/* Criado para convênio AVICENA de SP - HVC */
 
 
cd_tipo_atend_abramge_w	smallint;
qt_registros_matmed_w	integer;
qt_registros_propaci_w	integer;
nr_atend_original_w		bigint;


BEGIN 
cd_tipo_atend_abramge_w	:= 0;
 
/* 2 - Internado */
 
begin 
select	2 
into STRICT	cd_tipo_atend_abramge_w 
from	atend_paciente_unidade b, 
	conta_paciente a 
where	a.nr_interno_conta	= nr_interno_conta_p 
and	a.nr_atendimento	= b.nr_atendimento 
and	b.dt_entrada_unidade = 
	(SELECT max(x.dt_entrada_unidade) 
		from 	setor_atendimento y, 
			atend_paciente_unidade x		 
		where	x.nr_atendimento		= a.nr_atendimento 
		and	x.cd_setor_atendimento	= y.cd_setor_atendimento 
		and	y.cd_classif_setor in (3,4,8));
exception 
	when others then 
	cd_tipo_atend_abramge_w := 0;
end;
 
 
/* 1- Não internado e teve matmed ou procedimento diferente de consulta */
 
if (cd_tipo_atend_abramge_w = 0) then 
	begin 
	select	count(*) 
	into STRICT	qt_registros_matmed_w 
	from	material_atend_paciente 
	where	nr_interno_conta	= nr_interno_conta_p;
 
	select	count(*) 
	into STRICT	qt_registros_propaci_w 
	from	procedimento_paciente 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	cd_procedimento 	<> 10014;
	 
	if (qt_registros_matmed_w	> 0) or (qt_registros_propaci_w	> 0) then 
		cd_tipo_atend_abramge_w := 1;
	end if;
	end;
end if;
 
/* 0- Somente consulta ou retorno */
 
select	coalesce(max(b.nr_atend_original),0) 
into STRICT	nr_atend_original_w 
from 	atendimento_paciente b, 
	conta_paciente a 
where	a.nr_interno_conta	= nr_interno_conta_p 
and	a.nr_atendimento	= b.nr_atendimento;
 
if (nr_atend_original_w	> 0) then 
	cd_tipo_atend_abramge_w := 0;
end if;
 
 
RETURN cd_tipo_atend_abramge_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_atend_abramge ( nr_interno_conta_p bigint) FROM PUBLIC;

