CREATE OR REPLACE FUNCTION obter_tipo_atend_autor (nr_atendimento_p bigint, nr_seq_agenda_p bigint, nr_seq_agenda_cons_p bigint, nr_seq_age_integ_p bigint, nr_seq_paciente_setor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ie_tipo_atend_w		smallint;
nr_atendimento_w	bigint;
nr_seq_agenda_w		bigint;
nr_seq_agenda_consulta_w	bigint;

ds_retorno_w		varchar(255);

 
/* 
c- codigo 
d - descricao 
*/
 
 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	ie_tipo_atendimento 
	into STRICT	ie_tipo_atend_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
elsif (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (coalesce(ie_tipo_atend_w::text, '') = '' )then 
 
	select	ie_tipo_atendimento 
	into STRICT	ie_tipo_atend_w 
	from	agenda_paciente 
	where	nr_sequencia = nr_seq_agenda_p;
 
elsif (nr_seq_agenda_cons_p IS NOT NULL AND nr_seq_agenda_cons_p::text <> '')and (coalesce(ie_tipo_atend_w::text, '') = '' )then 
 
	select	ie_tipo_atendimento 
	into STRICT	ie_tipo_atend_w 
	from	agenda_consulta 
	where	nr_sequencia = nr_seq_agenda_cons_p;
elsif (nr_seq_age_integ_p IS NOT NULL AND nr_seq_age_integ_p::text <> '') and (coalesce(ie_tipo_atend_w::text, '') = '' )then 
 
	select	ie_tipo_atendimento 
	into STRICT	ie_tipo_atend_w 
	from	agenda_integrada 
	where	nr_sequencia = nr_seq_age_integ_p;
elsif (nr_seq_paciente_setor_p IS NOT NULL AND nr_seq_paciente_setor_p::text <> '') then 
	select	max(a.ie_tipo_atendimento) 
	into STRICT	ie_tipo_atend_w 
	from	paciente_setor_convenio a 
	where	a.nr_seq_paciente = nr_seq_paciente_setor_p;
		 
end if;
 
if (ie_opcao_p = 'D') then 
	ds_retorno_w := substr(obter_valor_dominio(12,ie_tipo_atend_w),1,255);
else 
	ds_retorno_w  := ie_tipo_atend_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
