CREATE OR REPLACE FUNCTION obter_tipo_atend_padrao_ge (ie_tipo_atend_padrao_ge_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(255)	:= 'Tipo de Atendimento=';
lista_tipo_atend_padrao_ge_w	varchar(255);
tam_lista_w				bigint;
ie_pos_virgula_w			smallint;
ie_tipo_atendimento_w		varchar(1);


BEGIN
if (ie_tipo_atend_padrao_ge_p IS NOT NULL AND ie_tipo_atend_padrao_ge_p::text <> '') then

	lista_tipo_atend_padrao_ge_w	:= ie_tipo_atend_padrao_ge_p;

	while	(lista_tipo_atend_padrao_ge_w IS NOT NULL AND lista_tipo_atend_padrao_ge_w::text <> '') loop

		tam_lista_w		:= length(lista_tipo_atend_padrao_ge_w);
		ie_pos_virgula_w	:= position(',' in lista_tipo_atend_padrao_ge_w);

		if (ie_pos_virgula_w <> 0) then
			ie_tipo_atendimento_w		:= substr(lista_tipo_atend_padrao_ge_w, 1, (ie_pos_virgula_w-1));
			lista_tipo_atend_padrao_ge_w	:= substr(lista_tipo_atend_padrao_ge_w, (ie_pos_virgula_w+1),

tam_lista_w);
			if (ds_retorno_w = 'Tipo de Atendimento=') then
				ds_retorno_w	:= ds_retorno_w || obter_valor_dominio(12, ie_tipo_atendimento_w);
			else	ds_retorno_w	:= ds_retorno_w ||','|| obter_valor_dominio(12, ie_tipo_atendimento_w);
			end if;
		elsif (tam_lista_w <> 0) then
			ie_tipo_atendimento_w		:= substr(lista_tipo_atend_padrao_ge_w, 1, 1);
			lista_tipo_atend_padrao_ge_w	:= null;
			if (ds_retorno_w = 'Tipo de Atendimento=') then
				ds_retorno_w	:= ds_retorno_w || obter_valor_dominio(12, ie_tipo_atendimento_w);
			else
				ds_retorno_w	:= ds_retorno_w ||','|| obter_valor_dominio(12, ie_tipo_atendimento_w);
			end if;
		end if;
	end loop;
end if;

RETURN	ds_retorno_w;

END	;
$body$
