ie_tipo_atendimento_w		protocolo.ie_tipo_atendimento%type;

BEGIN

select	coalesce(ie_tipo_atendimento,0)
into STRICT	ie_tipo_atendimento_w
from	protocolo
where	cd_protocolo in (SELECT	cd_protocolo
