CREATE OR REPLACE FUNCTION obter_tipo_atend_quimio (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
ie_tipo_atendimento_w	varchar(1);	
 

BEGIN 
 
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then  
	select	max(b.cd_pessoa_fisica) 	 
	into STRICT	cd_pessoa_fisica_w 
	from  paciente_setor b 
	where	b.nr_seq_paciente = nr_seq_paciente_p;
end if;
 
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then  
	select	coalesce(max('S'),'N') 	 
	into STRICT	ie_tipo_atendimento_w 
	from  atendimento_paciente 
	where	coalesce(dt_alta::text, '') = '' 
	and	cd_pessoa_fisica = cd_pessoa_fisica_w 
	and	ie_tipo_atendimento = 1;
end if;
 
	 
return	ie_tipo_atendimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
