SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_atributo ( nr_sequencia_p bigint, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE


ds_valor_dominio_w	varchar(15);
ie_tipo_atributo_w	varchar(10);

BEGIN



select 	max(ie_tipo_atributo)
into STRICT	ie_tipo_atributo_w
from 	tabela_atributo
where 	nm_tabela  = (	SELECT max(b.nm_tabela)
			from  tabela_visao b
			where b.nr_sequencia = nr_sequencia_p)
and 	nm_atributo = nm_atributo_p;

if (ie_tipo_atributo_w IS NOT NULL AND ie_tipo_atributo_w::text <> '') then
	select	max(ds_valor_dominio)
	into STRICT	ds_valor_dominio_w
	from 	valor_dominio
	where 	cd_dominio = 85
	and 	vl_dominio = ie_tipo_atributo_w;
end if;

return	ds_valor_dominio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
