CREATE OR REPLACE FUNCTION obter_tipo_baixa_mat_dev (nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_baixa_w		varchar(50);
nr_seq_tipo_baixa_w	bigint;
nr_prescricao_w		bigint;
nr_seq_prescr_w		integer;



BEGIN

SELECT	coalesce(MAX(nr_seq_tipo_baixa),0),
	MAX(nr_prescricao),
	MAX(nr_sequencia_prescricao)
INTO STRICT	nr_seq_tipo_baixa_w,
	nr_prescricao_w,
	nr_seq_prescr_w
FROM	material_atend_paciente
WHERE	nr_sequencia	= nr_seq_material_p;


IF (nr_seq_tipo_baixa_w > 0) THEN
	SELECT	ds_tipo_baixa
	INTO STRICT	ds_tipo_baixa_w
	FROM	tipo_baixa_prescricao
	WHERE	nr_sequencia		= nr_seq_tipo_baixa_w
	AND	ie_prescricao_devolucao	= 'D';
ELSE
	SELECT	b.ds_tipo_baixa
	INTO STRICT	ds_tipo_baixa_w
	FROM	tipo_baixa_prescricao b,
		prescr_material a
	WHERE	a.nr_prescricao		= nr_prescricao_w
	AND 	a.nr_sequencia		= nr_seq_prescr_w
	AND	a.cd_motivo_baixa		= b.cd_tipo_baixa
	AND	b.ie_prescricao_devolucao	= 'D';
END IF;

RETURN	ds_tipo_baixa_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
