CREATE OR REPLACE FUNCTION obter_tipo_baixa_prescr (cd_motivo_baixa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_baixa_normal_w	smallint;

BEGIN

if (cd_motivo_baixa_p <> 0) then
	select	count(*)
	into STRICT	ie_baixa_normal_w
	from	tipo_baixa_prescricao
	where	cd_tipo_baixa = cd_motivo_baixa_p
	  and	ie_prescricao_devolucao = 'P'
	  and	ie_conta_paciente = 'S';

	if (ie_baixa_normal_w = 0) then
		return 'C';
	else
		return 'N';
	end if;
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
