CREATE OR REPLACE FUNCTION obter_tipo_baixa_req_item ( cd_motivo_baixa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_baixa_w	varchar(80) := '';
cd_motivo_baixa_w	smallint;


BEGIN

if (coalesce(cd_motivo_baixa_p, 0) > 0) then

	select	max(ds_motivo_baixa)
	into STRICT	ds_motivo_baixa_w
	from	sup_motivo_baixa_req
	where	ie_situacao = 'A'
	and	cd_motivo_baixa = cd_motivo_baixa_p
	and	ie_req_conjunto = 'S';

	if (coalesce(ds_motivo_baixa_w::text, '') = '') then
		ds_motivo_baixa_w := substr(obter_valor_dominio(40, cd_motivo_baixa_p),1,80);
	end if;

end if;

return	ds_motivo_baixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
