CREATE OR REPLACE FUNCTION obter_tipo_carteira_regra (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_tipo_carteira_w	varchar(5)	:= null;
cd_tipo_portador_w	integer;
cd_portador_w		bigint;
cd_convenio_w		integer	:= null;
ie_tipo_convenio_w	smallint	:= null;

c01 CURSOR FOR 
SELECT	ie_tipo_carteira 
from	regra_carteira_cobr 
where	coalesce(cd_tipo_portador,cd_tipo_portador_w)	= cd_tipo_portador_w 
and	coalesce(cd_portador,cd_portador_w)			= cd_portador_w 
and	coalesce(ie_tipo_convenio,coalesce(ie_tipo_convenio_w,0))	= coalesce(ie_tipo_convenio_w,0) 
and	ie_situacao	= 'A' 
order by 
	coalesce(cd_portador,cd_portador_w), 
	coalesce(cd_tipo_portador,cd_tipo_portador_w), 
	coalesce(ie_tipo_convenio_w,0);


BEGIN 
 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
 
	select	cd_tipo_portador, 
		cd_portador 
	into STRICT	cd_tipo_portador_w, 
		cd_portador_w 
	from	titulo_receber 
	where	nr_titulo	= nr_titulo_p;
 
	cd_convenio_w	:= obter_convenio_tit_rec(nr_titulo_p);
 
	if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then 
 
		select	max(ie_tipo_convenio) 
		into STRICT	ie_tipo_convenio_w 
		from	convenio 
		where	cd_convenio	= cd_convenio_w;
	end if;
 
	open c01;
	loop 
	fetch c01 into 
		ie_tipo_carteira_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		null;
	end loop;
	close c01;
end if;
 
 
return ie_tipo_carteira_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
